package com.nearsoft.questions.domain;import javax.persistence.Cacheable;import javax.persistence.Column;import javax.persistence.Entity;import java.io.Serializable;@Entity@Cacheablepublic class Rule implements Serializable {    @Column(nullable = false)    private RuleName ruleName;    @Column(nullable = false)    private Integer points;    public RuleName getRuleName() {        return ruleName;    }    public void setRuleName(RuleName ruleName) {        this.ruleName = ruleName;    }    public Integer getPoints() {        return points;    }    public void setPoints(Integer points) {        this.points = points;    }}