package com.nearsoft.questions.domain;import javax.persistence.Cacheable;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.EnumType;import javax.persistence.Enumerated;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.SequenceGenerator;import java.io.Serializable;@Entity@Cacheablepublic class Rule implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "rule_seq")    @SequenceGenerator(name = "rule_seq", sequenceName = "rule_seq")    private Long id;    @Enumerated(EnumType.STRING)    @Column(nullable = false)    private RuleName ruleName;    @Column(nullable = false)    private Integer points;    public RuleName getRuleName() {        return ruleName;    }    public void setRuleName(RuleName ruleName) {        this.ruleName = ruleName;    }    public Integer getPoints() {        return points;    }    public void setPoints(Integer points) {        this.points = points;    }}