package com.nearsoft.questions.domain;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.EnumType;import javax.persistence.Enumerated;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.SequenceGenerator;import java.time.ZonedDateTime;import org.hibernate.annotations.Type;import org.springframework.data.annotation.CreatedDate;@Entitypublic class RuleAnswerTransaction {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "rule_answer_transaction_seq")    @SequenceGenerator(name = "rule_answer_transaction_seq", sequenceName = "rule_answer_transaction_seq", allocationSize = 1)    private Long id;    private Long answerId;    @Enumerated(EnumType.STRING)    @Column(name = "rule_name", length = 35, nullable = false)    private RuleName ruleName;    @Type(type = "org.jadira.usertype.dateandtime.threeten.PersistentZonedDateTime")    @CreatedDate    private ZonedDateTime createdAt;    private Integer points;    public Long getAnswerId() {        return answerId;    }    public ZonedDateTime getCreatedAt() {        return createdAt;    }    public Long getId() {        return id;    }    public Integer getPoints() {        return points;    }    public RuleName getRuleName() {        return ruleName;    }    public void setAnswerId(Long answerId) {        this.answerId = answerId;    }    public void setCreatedAt(ZonedDateTime createdAt) {        this.createdAt = createdAt;    }    public void setId(Long id) {        this.id = id;    }    public void setPoints(Integer points) {        this.points = points;    }    public void setRuleName(RuleName ruleName) {        this.ruleName = ruleName;    }}