package com.nearsoft.questions.domain;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.SequenceGenerator;import java.time.ZonedDateTime;import org.hibernate.annotations.Type;import org.springframework.data.annotation.CreatedDate;public class RuleQuestionTransaction {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "rule_question_transaction_seq")    @SequenceGenerator(name = "rule_question_transaction_seq", sequenceName = "rule_question_transaction_seq")    private Long id;    private Long questionId;    private String ruleName;    @Type(type = "org.jadira.usertype.dateandtime.threeten.PersistentZonedDateTime")    @CreatedDate    private ZonedDateTime createdAt;    private Integer points;    public Integer getPoints() {        return points;    }    public void setPoints(Integer points) {        this.points = points;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Long getQuestionId() {        return questionId;    }    public void setQuestionId(Long questionId) {        this.questionId = questionId;    }    public String getRuleName() {        return ruleName;    }    public void setRuleName(String ruleName) {        this.ruleName = ruleName;    }    public ZonedDateTime getCreatedAt() {        return createdAt;    }    public void setCreatedAt(ZonedDateTime createdAt) {        this.createdAt = createdAt;    }}